import React, { useContext, useState, useEffect } from 'react'
import Logo from './img/logo.svg'
import Styles from './Nav.module.css'
import { useNavigate } from 'react-router-dom'
import { Context } from '../Context'
import HamburgerMenu from './img/icon-hamburger.svg'
import CloseIcon from './img/icon-close.svg'

const Nav = () => {
  const { index, setIndex } = useContext(Context)
  const [isMobile, setIsMobile] = useState(false)
  const [isOpen, setIsOpen] = useState(false)
  const navigate = useNavigate()

  const handleResize = () => {
    if (window.innerWidth < 450) {
      setIsMobile(true)
    } else {
      setIsMobile(false)
    }
  }

  useEffect(() => {
    handleResize()
  }, [window.innerWidth])
  return (
    <div className={Styles['nav-container']}>
      <img
        src={Logo}
        alt='logo'
        onClick={() => {
          setIndex('0')
          navigate('/')
        }}
        className={Styles.logo}
      />
      <div className={Styles['nav-decoration']} />
      <div>
        <img
          src={HamburgerMenu}
          style={{ display: isMobile ? (isOpen ? 'none' : 'block') : 'none' }}
          onClick={() => setIsOpen(isOpen ? false : true)}
          alt='hamburgerMenu'
        />
        <ul className={isMobile ? Styles['nav-ul-mobile'] : Styles['nav-ul']}>
          <div>
            <img
              src={CloseIcon}
              onClick={() => setIsOpen(false)}
              style={{
                display: isMobile ? (isOpen ? 'block' : 'none') : 'none',
              }}
              alt='closeIcon'
            />
          </div>
          <NavComponent
            number='00'
            name='Home'
            style={
              Styles[
                index === '0' ? 'navComponent-list-active' : 'navComponent-list'
              ]
            }
            click={() => {
              setIndex('0')
              navigate('/')
            }}
          />
          <NavComponent
            number='01'
            name='Destination'
            style={
              Styles[
                index === '1' ? 'navComponent-list-active' : 'navComponent-list'
              ]
            }
            click={() => {
              setIndex('1')
              navigate('/destination')
            }}
          />
          <NavComponent
            number='02'
            name='Crew'
            style={
              Styles[
                index === '2' ? 'navComponent-list-active' : 'navComponent-list'
              ]
            }
            click={() => {
              setIndex('2')
              navigate('/crew')
            }}
          />
          <NavComponent
            number='03'
            name='Technology'
            style={
              Styles[
                index === '3' ? 'navComponent-list-active' : 'navComponent-list'
              ]
            }
            click={() => {
              setIndex('3')
              navigate('/technology')
            }}
          />
        </ul>
      </div>
    </div>
  )
}

const NavComponent = (props) => {
  return (
    <li className={props.style} onClick={props.click}>
      <span className={Styles['navComponent-list-number']}>{props.number}</span>
      <span className={Styles['navComponent-list-name']}> {props.name}</span>
    </li>
  )
}

export default Nav
